create table recipes(id int not null, recipe_number varchar(10) not null, recipe_name varchar(100) not null, description varchar(500), text clob, constraint recipes_pk primary key (id));
create sequence recipes_seq start with 1 increment by 1;
alter table recipes add constraint recipes_number_nn check (recipe_number is not null);
create table book_author(id int not null, last_name varchar(30), first_name varchar(30));
alter table book_author add constraint book_author_pk primary key (id);
create sequence book_author_seq start with 1 increment by 1;
create table book(id int primary key, title varchar(150), image varchar(150), description clob);
create sequence book_seq start with 1 increment by 1;
create table publication(id int primary key, author_id int not null, book_title varchar(500) not null, publish_date date, publish_co varchar(100));
create sequence publication_seq start with 1 increment by 1;
create table author_work(id int primary key, author_id int not null, chapter_number int not null, chapter_title varchar(100) not null, constraint author_work_fk foreign key(author_id) references book_author(id));
create sequence author_work_seq start with 1 increment by 1;
create table recipe_text (id int primary key, recipe_id int not null, text clob, constraint recipe_text_fk foreign key (recipe_id)references recipes(id));
create sequence recipe_text_seq start with 10 increment by 1;
